<?phpnamespace App\Libraries;use FPDF;class cetak_pdf extends FPDF{    public function __construct($orientation = 'P', $unit = 'mm', $size = 'A4')    {        parent::__construct($orientation, $unit, $size);    }    public function Header()    {        // Path to your logo - adjust according to your CI4 directory structure        $logoPath = ROOTPATH . 'public/images/logo-sl_toilet.jpg';                $this->Image($logoPath, 10, 8);        $this->SetFont("Arial", "B", 15);        $this->Cell(80);        $this->Cell(            30,            10,            "SISTEM LAPORAN TOILET",            0,            0,            "C"        );        $this->Ln(20);        $this->Line(10, 25, 200, 25);    }    public function Content()    {        $this->SetFont("Times", "", 12);        for ($i = 1; $i <= 40; $i++) {            $this->Cell(0, 10, "Sistem Laporan Toilet " . $i, 0, 1);        }    }    public function Footer()    {        $this->SetY(-15);        $this->Line(10, $this->GetY(), 200, $this->GetY());        $this->SetFont("Arial", "I", 9);        $this->Cell(            0,            10,            "Halaman " . $this->PageNo() . " dari {nb}",            0,            0,            "R"        );    }}