<?phpclass cetak_pdf{    function __construct()    {        include_once APPPATH . "/third_party/fpdf/fpdf.php";    }    function Header()    {        $this->Image("logo-sl_toilet.jpg", 10, 8);        $this->SetFont("Arial", "B", 15);        $this->Cell(80);        $this->Cell(            30,            10,            "SISTEM LAPORAN TOILET",            0,            0,            "C"        );        $this->Ln(20);        $this->Line(10, 25, 200, 25);    }    function Content()    {        $this->SetFont("Times", "", 12);        for ($i = 1; $i <= 40; $i++) {            $this->Cell(0, 10, "Sistem Laporan Toilet " . $i, 0, 1);        }    }    function Footer()    {        $this->SetY(-15);        $this->Line(10, $this->GetY(), 200, $this->GetY());        $this->SetFont("Arial", "I", 9);        $this->Cell(            0,            10,            "Halaman " . $this->PageNo() . " dari {nb}",            0,            0,            "R"        );    }}?>